public class InfinitiveDoWhileLoop {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		do{
			System.out.println("InfinitiveDoWhileLoop");
			System.out.println("Press Ctrl+C to stop");
		}
		while(true);	
		
	}

}
